mapper.sql:
		1=1

	       and sign_id = #{sign_id}


	       and sign_name  like CONCAT('%',#{sign_name},'%')



	       and sign_type = #{sign_type}



	       and sign_author = #{sign_author}



	       and sign_state = #{sign_state}



	       and upd_time >= #{upd_time}



	       and cre_time >= #{cre_time}



            and ${_sql_where}

mapper.update:
update sys_sign_info
   set sign_name=#{sign_name}
	  ,sign_type=#{sign_type}
	  ,sign_author=#{sign_author}
	  ,sign_state=#{sign_state}
	  ,cre_time=now()
	  ,upd_time=now()
 where sign_id = #{sign_id}

mapper.select:
select o.*,e.sign_type_name  sign_type_name from sys_sign_info o left join sign_type e
on o.sign_type =e.sign_type_id




	order by ${_sql_order}
