mapper.sql:
		1=1

	       and sign_id = #{sign_id}


	       and sms_id = #{sms_id}


	       and sms_title  like CONCAT('%',#{sms_title},'%')



	       and sms_content  like CONCAT('%',#{sms_content},'%')



	       and sms_rec_obj = #{sms_rec_obj}



	       and sms_author = #{sms_author}



	       and sms_state = #{sms_state}



	       and mould_id = #{mould_id}



	       and upd_time >= #{upd_time}



	       and cre_time >= #{cre_time}



            and ${_sql_where}

mapper.update:
update sys_sms_info
   set sign_id=#{sign_id}
	  ,sms_title=#{sms_title}
	  ,sms_content=#{sms_content}
	  ,sms_rec_obj=#{sms_rec_obj}
	  ,sms_author=#{sms_author}
	  ,sms_state=#{sms_state}
	  ,cre_time=now()
	  ,upd_time=now()
 where sms_id = #{sms_id}

mapper.select:
select o.*,s.sign_name as sign_name,m.mould_name as  mould_name from sys_sms_info o left join sys_sign_info s
on o.sign_id =s.sign_id left join sys_mould_info m on o.mould_id = m.mould_id




	order by ${_sql_order}
