mapper.update:
update user_department 
   set dep_global_id=#{dep_global_id}
	  ,sup_dep_global_id=#{sup_dep_global_id}
	  ,dep_name=#{dep_name}
	  ,dep_level=#{dep_level}
	  ,dep_state=#{dep_state}
	  ,cre_time=now()
	  ,upd_time=now()
 where dep_id = #{dep_id}

mapper.select:
select d.*,s.super_dep_name from user_department d left join (select dep_global_id id, dep_name super_dep_name from user_department) s on d.sup_dep_global_id=s.id
order by d.dep_global_id,d.dep_state desc,${_sql_order}
order by d.dep_global_id, d.dep_state desc
